import React, { useState } from 'react';
import axios from 'axios';

function AddProduct() {
    const [formData, setFormData] = useState({
        name: '',
        price: '',
        description: ''
    });

    const { name, price, description } = formData;

    const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = async (e) => {
        e.preventDefault();
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('token')}`
                }
            };
            const body = JSON.stringify(formData);
            const response = await axios.post('http://localhost:4000/api/products', body, config);
            console.log(response.data);
            // Optionally clear form or give feedback
        } catch (error) {
            console.error('Error adding product:', error.response);
        }
    };

    return (
        <form onSubmit={onSubmit}>
            <input type="text" placeholder="Name" name="name" value={name} onChange={onChange} required />
            <input type="text" placeholder="Price" name="price" value={price} onChange={onChange} required />
            <textarea placeholder="Description" name="description" value={description} onChange={onChange} required />
            <button type="submit">Add Product</button>
        </form>
    );
}

export default AddProduct;
