import React, { useEffect, useState } from 'react';
import axios from 'axios';
import deleteProduct from './deleteProducts';

function ProductsList() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const { data } = await axios.get('http://localhost:4000/api/products', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`
                    }
                });
                setProducts(data);
            } catch (error) {
                console.error('Failed to fetch products:', error.response);
            }
        };

        fetchProducts();
    }, []);
    const ProductList = ({ products, onSelectProduct, selectedProducts }) => (
        <ul>
          {products.map(product => (
            <li key={product._id}>
              <input
                type="checkbox"
                checked={selectedProducts.includes(product._id)}
                onChange={() => onSelectProduct(product._id)}
              />
              {product.name} - ${product.price}
            </li>
          ))}
        </ul>
      );


    return (
        <div>
            <ul>
                {products.map(product => (
                    <li key={product._id}>
                        {product.name}
                        <button onClick={() => deleteProduct(product._id, products, setProducts)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ProductsList;
