import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Login from './components/login';
import Signup from './components/signup';
import ProductsList from './components/productsList'; // Ensure this is the correct path to your component
import ProductManagementPage from './components/productManagement';

const App = () => {
  const [authToken, setAuthToken] = useState(localStorage.getItem('token'));

  const handleLoginSuccess = (token) => {
    localStorage.setItem('token', token); // Save token to local storage or state management
    setAuthToken(token); // Update state to reflect the new token
  };

  return (
    <Router>
      <nav>
        {/* Update Link paths and text accordingly */}
        <Link to="/">Home</Link> 
        {!authToken && <Link to="/login">Login</Link>}
        {!authToken && <Link to="/signup">Signup</Link>}
        {authToken && <Link to="/products">Product Management</Link>}
      </nav>
      <Routes>
        <Route path="/" element={<ProductsList />} /> {/* Set ProductsList as the home page */}
        <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />
        <Route path="/signup" element={<Signup />} />
        {/* Protect the ProductManagementPage route to require authentication */}
        <Route 
          path="/products" 
          element={
            authToken ? <ProductManagementPage /> : <Login onLoginSuccess={handleLoginSuccess} />
          } 
        />
        {/* Add redirect for any other route to ProductsList as a catch-all */}
        <Route path="*" element={<ProductsList />} />
      </Routes>
    </Router>
  );
};

export default App;
