import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {deleteProduct} from './deleteProducts';
import 'bootstrap/dist/css/bootstrap.min.css';

function ProductsList() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const { data } = await axios.get('http://localhost:4000/api/products', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`
                    }
                });
                setProducts(data);
            } catch (error) {
                console.error('Failed to fetch products:', error.response);
            }
        };

        fetchProducts();
    }, []);
    const ProductList = ({ products, onSelectProduct, selectedProducts }) => (
        <ul>
          {products.map(product => (
            <li key={product._id}>
              <input
                type="checkbox"
                checked={selectedProducts.includes(product._id)}
                onChange={() => onSelectProduct(product._id)}
              />
              {product.name} - ${product.price}
            </li>
          ))}
        </ul>
      );


      return (
        <div className="container mt-4">
            <h2>Products</h2>
            <div className="row">
                {products.map(product => (
                    <div className="col-md-4 mb-3" key={product._id}>
                        <div className="card">
                            <div className="card-body">
                                <h5 className="card-title">{product.name}</h5>
                                <p className="card-text">{product.description}</p>
                                <button onClick={() => deleteProduct(product._id, products, setProducts)} className="btn btn-danger">Delete</button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default ProductsList;
