import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const ProductsList = () => {
    const [products, setProducts] = useState([]);
    const [showLoginPrompt, setShowLoginPrompt] = useState(false);
    const token = localStorage.getItem('token');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:4000/api/products');
                setProducts(response.data);
            } catch (error) {
                console.error('Failed to fetch products:', error.response);
            }
        };

        fetchProducts();
    }, []);

    const deleteProduct = async (productId) => {
        if (!token) {
            console.error('Not authorized');
            return;
        }
        try {
            await axios.delete(`http://localhost:4000/api/products/${productId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setProducts(products.filter(product => product._id !== productId));
        } catch (error) {
            console.error('Error deleting product:', error.response);
        }
    };

    const handleAddProductClick = () => {
        console.log('Add New Product clicked');
        if (!token) {
            setShowLoginPrompt(true); // Set state to show the login prompt
            console.log('No token found, showing login prompt'); // Check if this logs
        } else {
            navigate('/add-product');
        }
    };

    return (
        <div className="container mt-4">
            <h1>Tersano Products</h1>
            <div className="mb-3">
                <button onClick={handleAddProductClick} className="btn btn-primary">
                    Add New Product
                </button>
                {showLoginPrompt && (
                    <div className="alert alert-warning mt-2">
                        Please <Link to="/Login">login</Link> to add products.
                    </div>
                )}
            </div>
            <div className="row">
                {products.map((product) => (
                    <div className="col-md-4 mb-3" key={product._id}>
                        <div className="card h-100">
                            <div className="card-body">
                                <h5 className="card-title">{product.name}</h5>
                                <p className="card-text">{product.description}</p>
                                <p className="card-text">${product.price}</p>
                                {token && (
                                    <button onClick={() => deleteProduct(product._id)} className="btn btn-danger">
                                        Delete
                                    </button>
                                )}
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ProductsList;
