import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

function AddProduct() {
    const [formData, setFormData] = useState({
        name: '',
        price: '',
        description: ''
    });
    const { name, price, description } = formData;
    const navigate = useNavigate();

    // Check if the user is logged in by looking for a token
    const token = localStorage.getItem('token');

    const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = async (e) => {
        e.preventDefault();
        if (!token) {
            alert('You must be logged in to add a product.');
            navigate('/login');  // Redirect to login if not logged in
            return;
        }
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`
                }
            };
            const body = JSON.stringify(formData);
            const response = await axios.post('http://localhost:4000/api/products', body, config);
            console.log(response.data);
            alert('Product added successfully!');
            navigate('/products'); // Redirect to products page after adding
        } catch (error) {
            console.error('Error adding product:', error.response);
        }
    };

    return (
        <div className="container mt-4">
            <h2>Add Product</h2>
            <form onSubmit={onSubmit}>
                <div className="form-group">
                    <label>Name</label>
                    <input type="text" className="form-control" placeholder="Name" name="name" value={name} onChange={onChange} required />
                </div>
                <div className="form-group">
                    <label>Price</label>
                    <input type="text" className="form-control" placeholder="Price" name="price" value={price} onChange={onChange} required />
                </div>
                <div className="form-group">
                    <label>Description</label>
                    <textarea className="form-control" placeholder="Description" name="description" value={description} onChange={onChange} required />
                </div>
                <button type="submit" className="btn btn-primary">Add Product</button>
            </form>
        </div>
    );
}

export default AddProduct;
